import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../../assets/styles/logincss/header.css';

const toggleDropdown = () => {
  const dropdown = document.getElementById('dropdownList');
  if (dropdown.style.display === 'block') {
    dropdown.style.display = 'none';
  } else {
    dropdown.style.display = 'block';
    document.addEventListener('click', handleDocumentClick);
  }
};
function Dummy() {
  const [dummyData, setDummyData] = useState([]);

  useEffect(() => {
    document.title = 'Logo';

    return () => {
      document.title = 'Default Title';
    };
  }, []);

  useEffect(() => {
    axios.get('http://localhost:9000/api/dummydata')
      .then((response) => {
        console.log('Data received:', response.data);
        setDummyData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  useEffect(() => {
    const handleDocumentClick = (event) => {
      const dropdown = document.getElementById('dropdownList');
      const image = document.getElementById('my_profile_img');

      if (!dropdown.contains(event.target) && event.target !== image) {
        dropdown.style.display = 'none';
        document.removeEventListener('click', handleDocumentClick);
      }
    };
    
    // const toggleDropdown = () => {
    //   const dropdown = document.getElementById('dropdownList');
    //   if (dropdown.style.display === 'block') {
    //     dropdown.style.display = 'none';
    //   } else {
    //     dropdown.style.display = 'block';
    //     document.addEventListener('click', handleDocumentClick);
    //   }
    // };

    return () => {
      document.removeEventListener('click', handleDocumentClick);
    };
  }, []); // Empty dependency array ensures that this effect runs once on mount and cleans up on unmount

  return (
    <div>
      {dummyData.map((item) => (
        <div key={item.id}>
          <button className="my_profile_img_button" onClick={toggleDropdown}>
            <img id="my_profile_img" className="clickable-image" alt="" src={`data:image/jpg;base64, ${item.profile}`} />
          </button>
          <div className="dropdown_main">
            <ul className="dropdown" id="dropdownList">
              <li>User name</li>
              <li>My Profile</li>
              <li>Dashboard Filter</li>
              <li>Logout</li>
              <li>Dayout/Logout</li>
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Dummy;
